import React, { useState } from "react";

function CreateArea() {
  const [inputTitle, setInputTitle] = useState("");
  const [inputContent, setInputContent] = useState("");

  function handleChange(event) {
    console.log(event.target);
  }

  return (
    <div>
      <form>
        <input name="title" placeholder="Title" onChange={handleChange} value={inputTitle} />
        <textarea name="content" placeholder="Take a note..." rows="3" onChange={handleChange} value={inputContent} />
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
